// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;

contract ArtPurchase {
    address payable owner; //Gallery360  
    
    struct Buyer {
        address buyerAddress;
        string email;
    }

    mapping (string => Buyer) buyerInfo;

    constructor() {
        owner = payable(msg.sender);
    }

    function buyArt(string memory _art_code, string memory _email) public payable {
        buyerInfo[_art_code] = Buyer(msg.sender, _email);
        owner.transfer(msg.value);
    }
}
